// calculate_rewards.js

const ROUND_MAX = 1096;

/**
 * 检查是否为有效数据（过滤掉默认值）
 */
function isValidValue(value, maxValidValue = Infinity) {
  return (
    value !== 255 &&
    value !== 65535 &&
    value !== 4294967295 &&
    value <= maxValidValue
  );
}

/**
 * 将u32原始金额转换为实际金额（考虑代币精度）
 */
function rawToAmount(rawAmount, tokenScale) {
  return rawAmount * tokenScale;
}

/**
 * 计算用户单轮奖励
 * 修复版本：所有输入值都应该是考虑了精度的整数
 */
function calculateUserReward(
  roundReward,
  roundStakeAmount,
  userStakeAmount,
  tokenScale
) {
  // 注意：所有参数都应该是考虑了 tokenScale 的整数值
  // 不需要额外的精度转换
  const totalStakeAmount = rawToAmount(roundStakeAmount, tokenScale);

  if (totalStakeAmount === 0) return 0;

  // 使用BigInt避免精度丢失
  try {
    const reward =
      (BigInt(userStakeAmount) * BigInt(roundReward)) /
      BigInt(totalStakeAmount);
    return Number(reward);
  } catch (error) {
    console.error("计算奖励时出错:", error);
    return 0;
  }
}

/**
 * 计算用户总奖励
 */
function calculateTotalReward(
  currentRoundIndex,
  poolState,
  poolStore,
  userState
) {
  // 参数验证
  if (!poolStore || typeof poolStore.len !== "number" || poolStore.len === 0) {
    console.log("poolStore 无效或为空");
    return 0;
  }

  if (
    !userState ||
    !Array.isArray(userState.stakes) ||
    userState.stakes.length === 0
  ) {
    console.log("userState 无效或用户无质押记录");
    return 0;
  }

  if (!poolState || !poolState.historyRoundRewards) {
    console.log("poolState 无效");
    return 0;
  }

  if (userState.is_unstaked) {
    console.log("用户已解押，不计算奖励");
    return 0;
  }

  const userStakes = userState.stakes;
  const effectiveLength = Math.min(poolStore.len, ROUND_MAX);

  // 安全访问数组数据
  const roundIndexes =
    poolStore.round_indexes && Array.isArray(poolStore.round_indexes)
      ? poolStore.round_indexes
          .slice(0, effectiveLength)
          .filter((idx) => isValidValue(idx))
      : [];

  if (roundIndexes.length === 0) {
    validRoundIndexes = [];
  } else {
    const lastIndex = roundIndexes[roundIndexes.length - 1];
    if (lastIndex === currentRoundIndex) {
      // 如果只有一个轮次，说明是首次质押，返回空数组
      if (roundIndexes.length === 1) {
        validRoundIndexes = [];
      } else {
        // 否则去掉最后一个
        validRoundIndexes = roundIndexes.slice(0, -1);
      }
    } else {
      validRoundIndexes = roundIndexes;
    }
  }

  const rewardIndexes =
    poolStore.reward_indexes && Array.isArray(poolStore.reward_indexes)
      ? poolStore.reward_indexes
          .slice(0, effectiveLength)
          .filter((idx) => isValidValue(idx))
      : [];
  const stakeAmounts =
    poolStore.stake_amounts && Array.isArray(poolStore.stake_amounts)
      ? poolStore.stake_amounts
          .slice(0, effectiveLength)
          .filter((amount) => isValidValue(amount))
      : [];

  if (roundIndexes.length === 0) {
    console.log("无有效轮次数据");
    return 0;
  }

  // 获取用户有效轮次
  let validUserStakes;
  const lastUserStake = userStakes[userStakes.length - 1];

  if (lastUserStake && lastUserStake.round_index === currentRoundIndex) {
    if (userStakes.length === 1) {
      return 0;
    } else {
      validUserStakes = userStakes.slice(0, userStakes.length - 1);
    }
  } else {
    validUserStakes = userStakes;
  }

  // 创建用户质押金额映射
  const userStakeMap = new Map();
  validUserStakes.forEach((stake) => {
    if (
      stake &&
      typeof stake.round_index === "number" &&
      typeof stake.stake_amount === "number"
    ) {
      userStakeMap.set(stake.round_index, stake.stake_amount);
    }
  });

  // 计算总收益
  let totalReward = 0;
  let lastUserStakeAmount = 0;
  let lastRoundReward = 0;

  // 遍历所有有效的轮次记录
  for (
    let actualIndex = 0;
    actualIndex < validRoundIndexes.length;
    actualIndex++
  ) {
    const naturalIndex = validRoundIndexes[actualIndex];
    const rewardIndex = rewardIndexes[actualIndex];
    const stakeAmount = stakeAmounts[actualIndex];

    // 跳过无效的奖励索引
    if (
      !isValidValue(rewardIndex) ||
      rewardIndex >= poolState.historyRoundRewards.length
    ) {
      continue;
    }

    // 首个快照不需要计算跳空值
    if (actualIndex !== 0) {
      const prevIndex = roundIndexes[actualIndex - 1];
      if (isValidValue(prevIndex)) {
        const roundSkipNum = naturalIndex - prevIndex;
        if (roundSkipNum > 1) {
          totalReward += (roundSkipNum - 1) * lastRoundReward;
        }
      }
    }

    // 确定当前轮次的用户质押金额
    let currentStakeAmount;
    if (userStakeMap.has(naturalIndex)) {
      currentStakeAmount = userStakeMap.get(naturalIndex);
      lastUserStakeAmount = currentStakeAmount;
    } else {
      if (lastUserStakeAmount === 0) {
        continue;
      } else {
        currentStakeAmount = lastUserStakeAmount;
      }
    }

    // 获取奖池金额
    const poolRoundReward = poolState.historyRoundRewards[rewardIndex];

    const reward = calculateUserReward(
      poolRoundReward,
      stakeAmount,
      currentStakeAmount,
      poolState.tokenScale
    );

    // 累加总奖励
    totalReward += reward;
    lastRoundReward = reward;
  }

  // 计算剩余轮次的奖励
  const lastRoundIndex = roundIndexes[roundIndexes.length - 1];

  if (isValidValue(lastRoundIndex) && currentRoundIndex > lastRoundIndex) {
    const remainderNaturalNum = currentRoundIndex - lastRoundIndex - 1;
    if (remainderNaturalNum > 0) {
      totalReward += lastRoundReward * remainderNaturalNum;
    }
  }

  return Math.floor(totalReward); // 确保返回整数
}

/**
 * 计算用户可领取奖励
 */
function calculateClaimableReward(
  currentRoundIndex,
  poolState,
  poolStore,
  userState
) {
  const totalReward = calculateTotalReward(
    currentRoundIndex,
    poolState,
    poolStore,
    userState
  );
  const claimedReward = userState.claimed_reward || 0;

  return Math.max(0, totalReward - claimedReward);
}

// 调试函数：显示计算详情
function debugCalculateReward(
  currentRoundIndex,
  poolState,
  poolStore,
  userState
) {
  console.log("=== 调试信息 ===");
  console.log("当前轮次:", currentRoundIndex);
  console.log("用户质押记录:", userState.stakes);
  console.log("奖池历史奖励:", poolState.historyRoundRewards);
  console.log(
    "有效轮次索引:",
    poolStore.round_indexes.slice(0, Math.min(poolStore.len, ROUND_MAX))
  );
  console.log(
    "奖励索引:",
    poolStore.reward_indexes.slice(0, Math.min(poolStore.len, ROUND_MAX))
  );
  console.log(
    "质押金额:",
    poolStore.stake_amounts.slice(0, Math.min(poolStore.len, ROUND_MAX))
  );

  const totalReward = calculateTotalReward(
    currentRoundIndex,
    poolState,
    poolStore,
    userState
  );
  const claimableReward = calculateClaimableReward(
    currentRoundIndex,
    poolState,
    poolStore,
    userState
  );

  console.log("计算的总奖励:", totalReward);
  console.log("可领取奖励:", claimableReward);
  console.log("================");

  return { totalReward, claimableReward };
}

